import React from "react"
class Item extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            edittask: {}
        }
    }
    hanbleEdit = (task) => {
        this.props.hanbleEdit(this.props.task)
        //console.log(task);

    }
    // onChangeEdit = (event) => {
    //     //console.log(event.target.value);
    //     console.log(event);

    // }


    render() {
        var { task, index, taskEdit } = this.props
        var objempty = Object.keys(taskEdit).length === 0
        // console.log(objempty);
        // console.log(taskEdit);
        return (
            <>
                <tr>
                    {objempty === true ?
                        <>
                            <td>{index + 1}</td>
                            <td >
                                <span onChange={(event) => this.onChangeEdit(event)} value={task.name}>{task.name}</span>
                            </td>
                            <td >
                                <span className={task.status === true ? 'text-danger' : 'text-success'}>{task.status === true ? 'kích hoạt' : 'ẩn'}</span>
                            </td> </> :
                        <>
                            <td>{index + 1}</td>
                            <td >
                                <span ><input onChange={(event) => this.onChangeEdit(event)} value={task.name} /></span>
                            </td>
                            <td >

                                <p className={task.status === true ? 'text-danger' : 'text-success'}>{task.status === true ? 'kích hoạt' : 'ẩn'}</p>
                            </td>
                        </>
                    }
                    <td className="text-center">
                        <button className="btn btn-success mx-2" onClick={() => this.hanbleEdit(task)}>
                            {objempty === true ? 'sửa' : 'xong'}
                        </button>
                        <button className="btn btn-danger">Xóa</button>
                    </td>
                </tr >
            </>



        )
    }
}

export default Item
