import React from "react"
class Item extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            edittask: {}
        }
    }
    hanbleEdit = (task) => {
        this.props.hanbleEdit(this.props.task)
        //console.log(task);

    }
    onChangeEdit = (event) => {
        //console.log(event.target.value);
        console.log(event);

    }

    updateStatus = () => {
        alert('check click')
        console.log('check click status');
    }
    render() {
        var { task, index } = this.props


        return (
            <tr>

                <td>{index + 1}</td>
                <td >
                    <span onChange={(event) => this.onChangeEdit(event)} value={task.name}>{task.name}</span>
                </td>
                <td >
                    <span className={task.status === true ? 'text-danger' : 'text-success'}
                        onClick={() => this.updateStatus}
                    >{task.status === true ? 'kích hoạt' : 'Ẩn'}</span>
                </td>


                <td className="text-center">
                    <button className="btn btn-success mx-2" >Sửa</button>
                    <button onClick={() => this.deleteItem} className="btn btn-danger">Xóa</button>
                </td>
            </tr >



        )
    }
}

export default Item
