import React from "react"
class Form extends React.Component {
constructor(props){
    super(props);
    this.state={
        tasks:{
            name:'',
            status:false
        }
    }
}
    
    closeForm=()=>{
        this.props.onAddForm()
    }
    onChange=(event)=>{

        this.setState({
            [event.target.name]:event.target.value
        })
    }
    addtask=()=>{
        console.log('check click add');
    }
    handleSubmit=(event)=>{
        console.log(this.state.value);
        event.preventDefault()
    }
    render() {
        
        return (
           
           <div className="panel">
            <div className=" panel_heading " onClick={()=>this.closeForm()}>
                <h3>Thêm Công Việc</h3>
            </div>
            <div className="panel_body">
            <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                    <label >Tên:</label><br />
                    <input type="text" className="form-control" 
                     name="name"
                     value={this.state.name}
                      onChange={(event)=>this.onChange(event)}/><br />
                    <label >Trạng thai:</label><br />
                    <select id="inputState" className="form-control"
                    value={this.state.status}
                    onChange={(event)=>this.onChange(event)}
                    >
                        <option value={false}>ẩn</option>
                        <option value={true}>Hiện</option>
                    </select>

                </div>
                <div className="panel_form_button">
                    <button type="button" className="btn btn-warning"
                    onClick={()=>this.addtask()}>Lưu</button>
                    <button type="submit" className="btn btn-danger">Hủy</button>
                </div>
            </form>
            </div>
            </div>
            

        )
    }
}

export default Form





