import React from 'react';
import './colorPick.scss';
import Stsize from './settingSize';
import Stcolor from './settingColor';
import Reset from './reset';
import Resfut from './resuft';

class Colorpick extends React.Component {
    constructor(props) {
        super(props);
        this.state = { color: 'red', fontSize: 12 };
    }

    onSetColorActive = (i) => {
        this.setState({
            color: i
        })
    }
    onSetSize = (size) => {
        this.setState({
            fontSize: (this.state.fontSize + size <= 8 && this.state.fontSize + size <= 36) ? this.state.fontSize + size : this.state.fontSize
        })
    }
    render() {
        return (
            <>
                <div className="container-fluid">
                    <div className="row">
                        <Stcolor color={this.state.color} payActiveColor={this.onSetColorActive} />
                        <div className="col-6">
                            <Stsize
                                fontsize={this.state.fontSize}
                                onSize={this.ize} />
                            <Reset />
                        </div>
                        <Resfut color={this.state.color} />
                    </div>
                </div>
            </>
        );
    }

}

export default Colorpick;
